pragma solidity ^0.5.0;

contract Payments {
    address payable public owner;
    address payable public recipient;

    uint public firstTransferTime;
    uint public secondTransferTime;
    uint public thirdTransferTime;

    bool public firstPaymentMade = false;  // To ensure the first payment is made only once

    // Constructor
    constructor() public {
        owner = 0x7dD6c89413f552e059879d51038FaD16ceD2d1A7;  // Owner address
        recipient = 0xEe4d083133856a76F56EFC667DD1D88610d44971;  // Recipient address

        // Set transfer times
        firstTransferTime = now;
        secondTransferTime = now + 2 minutes;
        thirdTransferTime = now + 3 minutes;
    }

    // Function to make the first payment of 5 ETH
    function makeFirstPayment() public {
        // Perform the first payment of 5 ETH to the recipient
        recipient.transfer(5 ether);

        // Mark the first payment as made
        firstPaymentMade = true;
    }

    // Function to transfer 5 ETH to the recipient after 2 minutes
    function transferSecond() public {
        require(now >= secondTransferTime, "2 minutes have not passed yet.");
        recipient.transfer(5 ether);
    }

    // Function to transfer 10 ETH to the recipient after 3 minutes
    function transferThird() public {
        require(now >= thirdTransferTime, "3 minutes have not passed yet.");
        recipient.transfer(10 ether);
    }

    // Fallback function to accept incoming ether
    function() external payable {}
}
